/*App Goals: 
-
-Cookie score tracker

*/
import React from 'react';
import ReactDOM from 'react-dom';
import './App.css';
import { BrowserRouter, Route, Link, Switch } from "react-router-dom"

function TitleScreen() {
  return (
    <>        
      <div className="TitleScreen">        
        <Link to="/play">
          <button id="playButton" >
            <h1>Click to Play</h1>
          </button>
        </Link>
      </div>          
    </>
  );
}

//

class PlayRound extends React.Component {  
  constructor() {
    super();
    this.state = {cpu: "", player: "", result: ""}
  }
  computerPlay = () => {
    let play = Math.floor(Math.random() * 3);
    switch(play){
        case 0: play = "ROCK"; break;
        case 1: play = "PAPER"; break;
        case 2: play = "SCISSORS"; break;
        default: console.log("Error");
    }
    this.setState({cpu: play});
  } 


  playRound = function(){
    //Game Logic
    switch(this.state.player){
        default:
            console.log("Error");
            break;
        case "ROCK":
            switch(this.state.cpu){
                case "ROCK":
                    this.setState({result: "Rock against rock; it's a tie game, try again"});
                    break;
                case "PAPER":
                    this.setState({result: "Paper beats rock, yeah I don't get it either. You lose though."});
                    break;
                case "SCISSORS":
                    this.setState({result: "Rock beats scissors, winner!"});
                    break;
            }
            break;
        case "PAPER":
            switch(this.state.cpu){
                case "ROCK":
                    this.setState({result: "Paper beats rock! Don't question it, you win!!"});
                    break;
                case "PAPER":
                    this.setState({result: "Any more paper and we'd have a small pamplet. It's a tie"});
                    break;
                case "SCISSORS":
                    this.setState({result: "Your paper got sliced. You lose. "});
                    break;
            }
            break;
        case "SCISSORS":
            switch(this.state.cpu){
                case "ROCK":
                  this.setState({result: "A rock smashed your scissors. You lose."});
                  break;
                case "PAPER":
                  this.setState({result: "Snippity dippity. You have the victory"});
                  break;
                case "SCISSORS":
                  this.setState({result: "Tie game, better look away."});
                  break;
            }                     
            break;

      }   
  }
  render() {    
    return (
      <>      
        <div className="Game">
          <h1>Rock, Paper, or Scissors?</h1>       

            <div className="ButtonContainer">
              <Link to="/result" >
                <button id="rock" value="ROCK" onClick={this.setState({player: this})}></button>
              </Link>
              <Link to="/result" >
                <button id="paper" value="PAPER" onClick={this.setState({player: this})}></button>
              </Link>
              <Link to="/result">
                <button id="scissors" value="SCISSORS" onClick={this.setState({player: this})}></button>
              </Link>          
            </div>

        </div>
      </>
    );
  }

}

function Result() {


  return (
    <>
      <div className="Result"></div>      
    </>
  );
}

function App() {  
  return (
    <>
    <BrowserRouter>
      <Switch>
        <Route exact path="/">
          <TitleScreen />
        </Route>
        <Route path="/play">
          <NewGame />
        </Route>
        <Route path="/result">
          <Result />
        </Route>
        <Route path="/stats">
          <Stats />
        </Route>
      </Switch>
    </BrowserRouter>
    </>
  )  
}

ReactDOM.render(<App />, document.getElementById("root"));


export default App;


