/*App Goals: 
-
-Cookie score tracker

*/
import React from 'react';
import './App.css';
import { Helmet } from 'react-helmet'

class Score extends React.Component{
  state = {
    playerScore: 0,
    cpuScore :0
  }

  addWin = () => this.setState({playerScore: this + 1});
  addLoss = () => this.setState({cpuScore: this + 1});
  getPlayerScore = () => {return this.state.playerScore}
  getCpuScore = () => {return this.state.cpuScore}

  render(){
    return <div className="Score">Player: {this.getPlayerScore()} Computer: {this.getCpuScore()}</div>;
  }
}

class Player extends React.Component {
  score = new Score(); 
  state = {gameScreen: ""}

  nextScreen = (result) => {
    const gameScreen = this.state.gameScreen;
    if(gameScreen !== "") {
      this.setState({gameScreen: result});
    }else{
      this.setState({gameScreen: "New Game"});
    }
  }

  render(){
    switch(this.state.gameScreen){
      default: return (
        <>
        <Helmet><title>Rock, Paper, Scissors</title></Helmet>
        <div className="Player">
          <h1>Rock Paper Scissors</h1>
          <button id="playButton" onClick={this.nextScreen()}>Play</button>
        </div>          
        </>
      );      
      case "New Game": return (
        <>
          <Score />
          <div className="Player" style={{backgroundImage:"#"}}></div>
        </>
      );
    }

  }
}

function App() {
  return (

    <Player />
  ); 
}

export default App;
