/*App Goals: 
-
-Cookie score tracker

*/
import React from 'react';
import './App.css';
import { ReactComponent } from '*.svg';
class Score extends ReactComponent{
  constructor() {
    super();
    this.state = {
      playerScore: 0,
      cpuScore :0
    }

  }
  addWin = () => this.state.playerScore += 1;  
  addLoss = () => this.state.cpuScore += 1;

  render(){
    <div className="Score">Player: {this.state.playerScore} Computer: {this.state.cpuScore}</div>
  }
}

class Player extends ReactComponent {
  playerScore = new Score();
  computerScore = new Score();
  render(){
    return (
      <img src="../images/Full_Icon.png" />
    );
  }
}

function App() {
  return (
    <div className="App">        
        <Player />
    </div>
  ); 
}

export default App;
