/*App Goals: 
-
-Cookie score tracker

*/
import React from 'react';
import ReactDOM from 'react-dom';
import './App.css';
import { Helmet } from 'react-helmet'
import { BrowserRouter, Route, Link, Switch } from "react-router-dom"

var player = "",
    cpu = "",
    result = "";

function TitleScreen() {
  return (
    <>  
      <Helmet><title>Rock Paper Scissors</title></Helmet>      
      <div className="TitleScreen">

        <Link to="/play">
          <button id="playButton" >
            <h1>Click to Play</h1>
          </button>
        </Link>
      </div>          
    </>
  );
}

//

function NewGame() {  
  playerSelect = () => {
    player = this.attr.value;
  }

  return (
    <>      
      <div className="Game">Rock, Paper, or Scissors?</div>
      <div className="ButtonContainer">
        <button value="ROCK" onClick={playerSelect}>
          <img src="rock.png" alt="rock" />
        </button>
        <button value="PAPER" onClick={playerSelect}>
          <img src="paper.png" alt="paper" />
        </button>
        <button value="SCISSORS" onClick={playerSelect}>
          <img src="scissors.png" alt="rock" />
        </button>
      </div>
    </>
  );
}

function Result() {
  var computerPlay = function(){
    let play = Math.floor(Math.random() * 3);
    switch(play){
        case 0: play = "ROCK"; break;
        case 1: play = "PAPER"; break;
        case 2: play = "SCISSORS"; break;
    }
    return play;
  }

  cpu = computerPlay();

  var playRound = function(){
    //Game Logic
    switch(player){
        default:
            console.log("Error");
            break;
        case "ROCK":
            switch(cpu){
                case "ROCK":
                    result = "Rock against rock; it's a tie game, try again";
                    break;
                case "PAPER":
                    result = "Paper beats rock, yeah I don't get it either. You lose though.";
                    break;
                case "SCISSORS":
                    result = "Rock beats scissors, winner!";
                    break;
            }
            break;
        case "PAPER":
            switch(cpu){
                case "ROCK":
                    result = "Paper beats rock! Don't question it, you win!!";
                    break;
                case "PAPER":
                    result = "Any more paper and we'd have a small pamplet. It's a tie";
                    break;
                case "SCISSORS":
                    result = "Your paper got sliced. You lose. ";
                    break;
            }
            break;
        case "SCISSORS":
            switch(cpu){
                case "ROCK":
                    result = "A rock smashed your scissors. You lose.";
                    break;
                case "PAPER":
                    result = "Snippity dippity. You have the victory";
                    break;
                case "SCISSORS":
                    result = "Tie game, better look away.";
                    break;
            }                     
            break;
      }   
    }

}

function Stats() {

}



function App() {  
  return (
    <>
    <BrowserRouter>
      <Switch>
        <Route exact path="/">
          <TitleScreen />
        </Route>
        <Route path="/play">
          <NewGame />
        </Route>
        <Route path="/result">
          <Result />
        </Route>
        <Route path="/stats">
          <Stats />
        </Route>
      </Switch>
    </BrowserRouter>
    </>
  )  
}

ReactDOM.render(<App />, document.getElementById("root"));


export default App;


